package nl.maartenwiegers.aoc2021;

import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

import java.util.stream.Stream;

import static org.junit.jupiter.api.Assertions.assertEquals;

public class Day14Test {

    private final Day14 day14 = new Day14();

    @ParameterizedTest
    @MethodSource("getPolymerArguments")
    public void shouldGetCorrectPolymer(String filename, int numberOfSteps, String expected) {
        assertEquals(expected, day14.getPolymerAfterSteps(filename, numberOfSteps));
    }

    @ParameterizedTest
    @MethodSource("getScoreArguments")
    public void shouldGetCorrectScore(String filename, int numberOfSteps, int expected) {
        assertEquals(expected, day14.getScore(filename, numberOfSteps));
    }

    private static Stream<Arguments> getPolymerArguments() {
        return Stream.of(Arguments.of("example", 1, "NCNBCHB"), Arguments.of("example", 2, "NBCCNBBBCBHCB"), Arguments.of("example", 3, "NBBBCNCCNBBNBNBBCHBHHBCHB"), Arguments.of("example", 4, "NBBNBNBBCCNBCNCCNBBNBBNBBBNBBNBBCBHCBHHNHCBBCBHCB"), Arguments.of("puzzleinput", 10, ""));
    }

    private static Stream<Arguments> getScoreArguments() {
        return Stream.of(Arguments.of("example", 10, 1588), Arguments.of("puzzleinput", 10, 2740));
    }
}
